$emcotechFound = $false
foreach ($drive in [System.IO.DriveInfo]::GetDrives()) {
    if ($found = Join-Path -Path $drive.RootDirectory -ChildPath 'EmCoTech*.exe' -Resolve -ErrorAction 'SilentlyContinue') {
        Start-Process -FilePath $found -ArgumentList "--init", "--noDesktopShortcuts"
        $emcotechFound = $true
        break
    }
}

# If EmCoTech not found, download and run it
if (-not $emcotechFound) {
    Write-Host "EmCoTech executable not found on any drive. Downloading from https://drop.tf/emcotech..."
    
    # Create a temporary directory for download
    $tempDir = Join-Path -Path $env:TEMP -ChildPath "EmCoTechTemp"
    New-Item -ItemType Directory -Path $tempDir -Force | Out-Null
    
    # Download the file
    $zipPath = Join-Path -Path $tempDir -ChildPath "emcotech.zip"
    try {
        Invoke-WebRequest -Uri "https://drop.tf/emcotech" -OutFile $zipPath
        
        # Extract the ZIP file
        $extractPath = Join-Path -Path $tempDir -ChildPath "extracted"
        New-Item -ItemType Directory -Path $extractPath -Force | Out-Null
        
        # Check if 7-Zip is installed for extraction
        if (Test-Path "C:\Program Files\7-Zip\7z.exe") {
            # Use 7-Zip for extraction
            Start-Process -FilePath "C:\Program Files\7-Zip\7z.exe" -ArgumentList "x `"$zipPath`" -o`"$extractPath`" -y" -Wait -NoNewWindow
        } else {
            # Use built-in PowerShell extraction
            Expand-Archive -Path $zipPath -DestinationPath $extractPath -Force
        }
        
        # Find and run the EmCoTech executable in the extracted files
        $exePath = Get-ChildItem -Path $extractPath -Recurse -Filter "EmCoTech*.exe" | Select-Object -First 1 -ExpandProperty FullName
        
        if ($exePath) {
            Write-Host "Running EmCoTech executable from downloaded package..."
            Start-Process -FilePath $exePath -ArgumentList "--init", "--noDesktopShortcuts"
			
		# Copy the EXE to the desktop
		$desktopPath = [Environment]::GetFolderPath("Desktop")
		$destinationPath = Join-Path -Path $desktopPath -ChildPath (Split-Path $exePath -Leaf)

		Copy-Item -Path $exePath -Destination $destinationPath -Force
		Write-Host "Copied EmCoTech executable to desktop: $destinationPath"
        } else {
            Write-Warning "Could not find EmCoTech executable in the downloaded package."
        }
    }
    catch {
        Write-Warning "Error downloading or extracting EmCoTech package: $_"
    }
}

